//Add Message

function attemptAddMessage(message) {
  return {
    type: 'ATTEMPT_ADD_MESSAGE',
    payload: message
  };
}

function addMessage(messageContent) {
  return function (dispatch, getState) {
    dispatch(attemptAddMessage(messageContent));
    var messageSender = getState().auth.account ? getState().auth.account.username : 'guest';
    console.log('message sender is ', messageSender);
    messagesRef.push({ details: messageContent, sender: messageSender }, function () {
      dispatch(receiveAddMessage(messageContent));
    });
  };
}

function receiveAddMessage(message) {
  return {
    type: 'RECEIVE_ADD_MESSAGE',
    payload: message
  };
}

//Load Messages

function attemptLoadMessages() {
  return {
    type: 'ATTEMPT_LOAD_MESSAGES'
  };
}

function loadMessages() {
  return function (dispatch, getState) {
    dispatch(attemptLoadMessages());
    messagesRef.on('value', function (messagesSnap) {
      console.log('messages loaded from firebase ', messagesSnap.val());
      var messagesList = [];
      messagesSnap.forEach(function (messageSnap) {
        //data from firebase
        var message = messageSnap.val();
        message.id = messageSnap.key();
        messagesList.push(message);
      });
      console.log('the messages list is: ', messagesList);
      dispatch(receiveLoadMessages(messagesList));
    });
  };
}

function receiveLoadMessages(messages) {
  //Messages is plural bc working on behalf of whole state
  return {
    type: 'RECEIVE_LOAD_MESSAGES',
    payload: messages
  };
}

//Remove Message

function attemptRemoveMessage(message) {
  return {
    type: 'ATTEMPT_REMOVE_MESSAGE'
  };
}

function removeMessage(id) {
  return function (dispatch, getState) {
    dispatch(attemptRemoveMessage());
    messagesRef.child(id).remove(function (messagesSnap) {
      console.log('message loaded from firebase ');
      dispatch(receiveRemoveMessage());
    });
  };
}

function receiveRemoveMessage(message) {
  //Messages is plural bc working on behalf of whole state
  return {
    type: 'RECEIVE_REMOVE_MESSAGE',
    payload: message
  };
}

function updateMessage(message) {
  return {
    type: 'UPDATE_MESSAGE',
    payload: message
  };
}

